var request=document.querySelector(".modal-request"),link=document.querySelector(".request-btn"),close=request.querySelector(".modal-close"),login=request.querySelector("[name=login]"),mail=request.querySelector("[name=mail]"),text=request.querySelector("[name=text]"),form=request.querySelector("form");link.addEventListener("click",function(e){e.preventDefault(),request.classList.add("modal-show"),login.focus()}),form.addEventListener("submit",function(e){login.value&&mail.value&&text.value||(e.preventDefault(),request.classList.add("modal-error"))}),close.addEventListener("click",function(e){e.preventDefault(),request.classList.remove("modal-show"),request.classList.remove("modal-error")});